// import navbar component:
// useSTate fro users:
import {useState} from 'react';
import Navbar from "./components/Navbar";
import Button from "./components/Button";
import * as ReactBootStrap from 'react-bootstrap';

function App() {
    const [user,setUser]=useState([]);
    const [dataLoading,setDataLoading]=useState(false);

    // asynchronous method:
    const loadUser=async()=>{
        // wait till response is fetched from API:
        const response=await fetch ("https://reqres.in/api/users");
        setDataLoaded(true);
        // Convert response to JSON for usage:
        const jsonResponse=await response.json();
        // Extract only data array from jsonResponse:
        const item=jsonResponse.data;
        // Now setting the Users with data fetched from API:
        setUser(item);
    };

    return (
         <div >
        <Navbar/>
        <br/>
        <hr></hr>
        <Button type="submit" onClick={loadUser}>Get User!!</Button>
        <br/><br/><br/>
        <ul className="userData">
            {dataLoaded ? user.map(({id,email,first_name,last_name,avatar})=>(
                <li key={id}>
                    <img src={avatar}></img>
                    <p className="mail">E-MAil : {email}</p>
                    <p className="name"> Full Name: {first_name} {last_name}</p>
                    </li>
            )):<ReactBootStrap.Spinner animation="grow" />}
        </ul>
        </div>
    );
}

export default App;
